% CheckSystemConfig.m% expt.m% Run Experiment ...% after checking Computer Hardware, Software, and Graphics%% System Configuration should be:% 	Mac OS 9.2.2% 	Matlab 5.2.1.1421% 	Psychtoolbox 2.55, 12 March 2005% 	File Sharing is OFF% 	Virtual Memory is OFF% 	Monitor is 2304 x 1440,  80 Hz% return value%	err = 0		system config okay%	err = 1		warnings%	err = 2		errorsfunction err = CheckSystemConfig(strict)if nargin == 0	strict = 1;end	if strict == 1	err = 0;else    fprintf('WARNING: strict system check is turned off\n');	err = 1;endfprintf('System Configuration:\n');%% Operating systemcomp = Screen('Computer');if comp.runningOnClassic	fprintf('\t%s (Classic)\n', comp.system);else	fprintf('\t%s\n', comp.system);endif comp.runningOnClassic	if strict		fprintf('\nERROR: running in Classic mode\n');    	fprintf('\tBefore proceeding with the experiment, ...\n\n');	    fprintf('\treboot computer in Mac OS 9.2.2\n');		err = 2;		return;	else		fprintf('\nWARNING: runnning in Classic mode\n');		fprintf('\tExperiment timing may not be accurate.\n\n');		err =  1;	endend%% Matlab versionfprintf('\tMatlab %s\n', version);%% Psychtoolbox versionif isnumeric(PsychtoolboxVersion)    s = sprintf('%.2f',PsychtoolboxVersion);else    s = PsychtoolboxVersion;endfprintf('\tPsychtoolbox %s, %s\n', s, PsychtoolboxDate);%% File Sharingif IsOS9    fprintf('\tFile Sharing is ');	try    	F = FS('Check') > -3;		if F        	fprintf('ON\n');    	else        	fprintf('OFF\n');    	end	catch		fprintf('UNKNOWN - error checking file sharing status\n');		F = 1;	end        if F & strict        fprintf('\nERROR: File Sharing must be turned off\n');        fprintf('\tbefore proceeding with the experiment.\n\n');        fprintf('\tOpen File Sharing Control Panel, and turn it off.\n');        return;    end        fprintf('\tVirtual Memory is ');    g = gestalt('vm  ');    VM = (length(g) == 32) & any(g);    if VM        fprintf('ON\n');    else        fprintf('OFF\n');    end        if VM & strict        fprintf('\nERROR: Virtual Memory must be turned off\n');        fprintf('\tbefore proceeding with the experiment.\n\n');        fprintf('\tOpen Memory Control Panel, and turn it off.\n');        return;    end        res = Screen(0,'Resolution');    fprintf('\tMonitor is %d x %d,  %.1f Hz\n', res.width, res.height, res.hz);    VALID = (res.width == 2304) & (res.height == 1440) & (res.hz == 80);    if ~VALID		if strict	        fprintf('\nERROR: The Monitor settings are wrong\n')	        fprintf('\tand must be set to 800 x 600, 120 Hz\n');	        fprintf('\tbefore proceeding with the experiment.\n\n');	        fprintf('\tOpen Monitors Control Panel and select proper settings.\n');			err = 2;	        return;		else			fprintf('\nWARNING: Monitor settings different from expected.\n');			fprintf('\tExpected resolution is 2304 x 1440, 80 Hz\n');			fprintf('\tOpen Monitors Control Panel and select proper settings.\n\n');			err = 1;		end    endend%% Home Directoryhome = pwd;folder = [ 'O''Hara' filesep 'Users' filesep 'djones' filesep 'Matlab' filesep 'JumpingStand' filesep ];end